tinymce.PluginManager.add('equa', function(editor, url) {
    editor.addButton('equa', {
        title: 'Equation',
        image: url+'/equa.png',
        tooltip: 'Insert Equation',
        onclick: function() {
            editor.windowManager.open({
                title: 'Latex',
                body: [
                    // {type: 'container', name: 'preview', label: 'Preview',size: 60},
                    {type: 'textbox', name: 'latex', label: 'Latex',size: 60}
                ],
                onsubmit: function(e) {
                    var urim='https://chart.googleapis.com/chart?cht=tx&chf=a,s,000000|bg,s,FFFFFF00&chl=' + encodeURIComponent(e.data.latex);
                    editor.insertContent('<img src="' +urim+'">' );
                }
            });
        }
    });
});